% smooth log(infile) and reduce number of points to 1/2%infile=input('name of 1D file to be smoothed and halved -->');function [temp] = smoothlog(In, rdc)logfile=log(In);for k = 1:rdc    nxs=length(logfile);    nxsd2=round(nxs/2);    if(nxs==nxsd2*2)   %even nxs      for i=2:2:nxs-2       id2=i/2;       rdc_temp(id2)=0.25*logfile(i-1)+0.5*logfile(i)+0.25*logfile(i+1);      end      rdc_temp(nxs/2)=0.5*logfile(nxs-1)+0.5*logfile(nxs);    else				%odd nxs      for i=2:2:nxs-1       id2=i/2;       rdc_temp(id2)=0.25*logfile(i-1)+0.5*logfile(i)+0.25*logfile(i+1);      end    end    clear logfile    logfile = rdc_temp;    clear rdc_tempendtemp=exp(logfile);end